	}
	$templateCode .= loadView('event_table-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: event_table_dv
	if(function_exists('event_table_dv')) {
		$args = [];
		event_table_dv(($hasSelectedId ? $selectedId : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
