function meetings_decision_table_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('meetings_decision_table');
	if(!$arrPerm['insert']) {
		$error_message = $Translation['no insert permission'];
		return false;
	}

	// automatic agenda_lookup if passed as filterer
	if(Request::val('filterer_agenda_lookup')) {
		$_REQUEST['agenda_lookup'] = Request::val('filterer_agenda_lookup');
	}

	$data = [
		'decision_description' => br2nl(Request::val('decision_description', '')),
		'decision_actor' => Request::lookup('decision_actor', ''),
		'action_taken_with_date' => Request::dateComponents('action_taken_with_date', ''),
		'decision_status' => Request::val('decision_status', 'Yet to Start'),
		'decision_status_update_date' => Request::dateComponents('decision_status_update_date', ''),
		'decision_status_remarks_by_superior' => Request::val('decision_status_remarks_by_superior', ''),
		'created_by' => parseCode('<%%creatorUsername%%>', true),
		'created_at' => parseCode('<%%creationDateTime%%>', true),
	];


	// automatic agenda_lookup if passed as filterer
	if(Request::val('filterer_agenda_lookup')) {
		$data['agenda_lookup'] = Request::val('filterer_agenda_lookup');
	}
	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('meetings_decision_table', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		meetings_decision_table_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

function meetings_decision_table_copy_children($destination_id, $source_id) {
	global $Translation;
	$requests = []; // array of curl handlers for launching insert requests
	$eo = ['silentErrors' => true];
	$safe_sid = makeSafe($source_id);
