		if(!Request::val('Embedded')) $templateCode = str_replace('<%%DVPRINT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="dvprint" name="dvprint_x" value="1" title="' . html_attr($Translation['Print Preview']) . '"><i class="glyphicon glyphicon-print"></i> ' . $Translation['Print Preview'] . '</button>', $templateCode);
		if($allowUpdate)
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '<button type="submit" class="btn btn-success btn-lg" id="update" name="update_x" value="1" title="' . html_attr($Translation['Save Changes']) . '"><i class="glyphicon glyphicon-ok"></i> ' . $Translation['Save Changes'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);

		if($allowDelete)
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '<button type="submit" class="btn btn-danger" id="delete" name="delete_x" value="1" title="' . html_attr($Translation['Delete']) . '"><i class="glyphicon glyphicon-trash"></i> ' . $Translation['Delete'] . '</button>', $templateCode);
		else
			$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '<button type="submit" class="btn btn-default" id="deselect" name="deselect_x" value="1" onclick="' . $backAction . '" title="' . html_attr($Translation['Back']) . '"><i class="glyphicon glyphicon-chevron-left"></i> ' . $Translation['Back'] . '</button>', $templateCode);
	} else {
		$templateCode = str_replace('<%%UPDATE_BUTTON%%>', '', $templateCode);
		$templateCode = str_replace('<%%DELETE_BUTTON%%>', '', $templateCode);

		// if not in embedded mode and user has insert only but no view/update/delete,
		// remove 'back' button
		if(
			$allowInsert
			&& !$allowUpdate && !$allowDelete && !$arrPerm['view']
			&& !Request::val('Embedded')
		)
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
		elseif($separateDV)
			$templateCode = str_replace(
				'<%%DESELECT_BUTTON%%>', 
				'<button
					type="submit" 
					class="btn btn-default" 
					id="deselect" 
					name="deselect_x" 
					value="1" 
					onclick="' . $backAction . '" 
					title="' . html_attr($Translation['Back']) . '">
						<i class="glyphicon glyphicon-chevron-left"></i> ' .
						$Translation['Back'] .
				'</button>',
				$templateCode
			);
		else
			$templateCode = str_replace('<%%DESELECT_BUTTON%%>', '', $templateCode);
	}

	// set records to read only if user can't insert new records and can't edit current record
	if(!$fieldsAreEditable) {
		$jsReadOnly = '';
		$jsReadOnly .= "\t\$j('#decision_description').replaceWith('<div class=\"form-control-static\" id=\"decision_description\">' + (\$j('#decision_description').val() || '') + '</div>');\n";
		$jsReadOnly .= "\t\$j('#decision_actor').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\t\$j('#decision_actor_caption').prop('disabled', true).css({ color: '#555', backgroundColor: 'white' });\n";
		$jsReadOnly .= "\t\$j('#action_taken_with_date').prop('readonly', true);\n";
		$jsReadOnly .= "\t\$j('#action_taken_with_dateDay, #action_taken_with_dateMonth, #action_taken_with_dateYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\t\$j('#decision_status').replaceWith('<div class=\"form-control-static\" id=\"decision_status\">' + (\$j('#decision_status').val() || '') + '</div>'); \$j('#decision_status-multi-selection-help').hide();\n";
		$jsReadOnly .= "\t\$j('#decision_status_update_date').prop('readonly', true);\n";
		$jsReadOnly .= "\t\$j('#decision_status_update_dateDay, #decision_status_update_dateMonth, #decision_status_update_dateYear').prop('disabled', true).css({ color: '#555', backgroundColor: '#fff' });\n";
		$jsReadOnly .= "\t\$j('#decision_status_remarks_by_superior').replaceWith('<div class=\"form-control-static\" id=\"decision_status_remarks_by_superior\">' + (\$j('#decision_status_remarks_by_superior').val() || '') + '</div>');\n";
		$jsReadOnly .= "\t\$j('.select2-container').hide();\n";

		$noUploads = true;
	} else {
		// temporarily disable form change handler till time and datetime pickers are enabled
		$jsEditable = "\t\$j('form').eq(0).data('already_changed', true);";
		$jsEditable .= "\t\$j('form').eq(0).data('already_changed', false);"; // re-enable form change handler
	}

	// process combos
	$templateCode = str_replace('<%%COMBO(agenda_lookup)%%>', $combo_agenda_lookup->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(agenda_lookup)%%>', $combo_agenda_lookup->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(agenda_lookup)%%>', urlencode($combo_agenda_lookup->MatchText), $templateCode);
	$templateCode = str_replace('<%%COMBO(decision_actor)%%>', $combo_decision_actor->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(decision_actor)%%>', $combo_decision_actor->MatchText, $templateCode);
	$templateCode = str_replace('<%%URLCOMBOTEXT(decision_actor)%%>', urlencode($combo_decision_actor->MatchText), $templateCode);
	$templateCode = str_replace(
		'<%%COMBO(action_taken_with_date)%%>', 
		(!$fieldsAreEditable ? 
			'<div class="form-control-static">' . $combo_action_taken_with_date->GetHTML(true) . '</div>' : 
			$combo_action_taken_with_date->GetHTML()
		), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(action_taken_with_date)%%>', $combo_action_taken_with_date->GetHTML(true), $templateCode);
	$templateCode = str_replace('<%%COMBO(decision_status)%%>', $combo_decision_status->HTML, $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(decision_status)%%>', $combo_decision_status->SelectedData, $templateCode);
	$templateCode = str_replace(
		'<%%COMBO(decision_status_update_date)%%>', 
		(!$fieldsAreEditable ? 
			'<div class="form-control-static">' . $combo_decision_status_update_date->GetHTML(true) . '</div>' : 
			$combo_decision_status_update_date->GetHTML()
		), $templateCode);
	$templateCode = str_replace('<%%COMBOTEXT(decision_status_update_date)%%>', $combo_decision_status_update_date->GetHTML(true), $templateCode);

	/* lookup fields array: 'lookup field name' => ['parent table name', 'lookup field caption'] */
	$lookup_fields = ['agenda_lookup' => ['meetings_agenda_table', 'Agenda of Meeting'], 'decision_actor' => ['user_table', 'Decision actor'], ];
	foreach($lookup_fields as $luf => $ptfc) {
		$pt_perm = getTablePermissions($ptfc[0]);

		// process foreign key links
		if(($pt_perm['view'] && isDetailViewEnabled($ptfc[0])) || $pt_perm['edit']) {
			$templateCode = str_replace("<%%PLINK({$luf})%%>", '<button type="button" class="btn btn-default view_parent" id="' . $ptfc[0] . '_view_parent" title="' . html_attr($Translation['View'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-eye-open"></i></button>', $templateCode);
		}

		// if user has insert permission to parent table of a lookup field, put an add new button
		if($pt_perm['insert'] /* && !Request::val('Embedded')*/) {
			$templateCode = str_replace("<%%ADDNEW({$ptfc[0]})%%>", '<button type="button" class="btn btn-default add_new_parent" id="' . $ptfc[0] . '_add_new" title="' . html_attr($Translation['Add New'] . ' ' . $ptfc[1]) . '"><i class="glyphicon glyphicon-plus text-success"></i></button>', $templateCode);
		}
	}

	// process images
	$templateCode = str_replace('<%%UPLOADFILE(id)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(agenda_lookup)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(decision_description)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(decision_actor)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(action_taken_with_date)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(decision_status)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(decision_status_update_date)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(decision_status_remarks_by_superior)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(created_by)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(created_at)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(last_updated_by)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(last_updated_at)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(meetings_decision_str)%%>', '', $templateCode);
	$templateCode = str_replace('<%%UPLOADFILE(tenant_id)%%>', '', $templateCode);

	// process values
	if($hasSelectedId) {
		$templateCode = str_replace('<%%VALUE(id)%%>', safe_html($urow['id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode($urow['id']), $templateCode);
		$templateCode = str_replace('<%%VALUE(agenda_lookup)%%>', safe_html($urow['agenda_lookup']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(agenda_lookup)%%>', urlencode($urow['agenda_lookup']), $templateCode);
		$templateCode = str_replace('<%%VALUE(decision_description)%%>', safe_html($urow['decision_description'], $fieldsAreEditable), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_description)%%>', urlencode($urow['decision_description']), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(decision_actor)%%>', safe_html($urow['decision_actor']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(decision_actor)%%>', html_attr($row['decision_actor']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_actor)%%>', urlencode($urow['decision_actor']), $templateCode);
		$templateCode = str_replace('<%%VALUE(action_taken_with_date)%%>', app_datetime($row['action_taken_with_date']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(action_taken_with_date)%%>', urlencode(app_datetime($urow['action_taken_with_date'])), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(decision_status)%%>', safe_html($urow['decision_status']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(decision_status)%%>', html_attr($row['decision_status']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_status)%%>', urlencode($urow['decision_status']), $templateCode);
		$templateCode = str_replace('<%%VALUE(decision_status_update_date)%%>', app_datetime($row['decision_status_update_date']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_status_update_date)%%>', urlencode(app_datetime($urow['decision_status_update_date'])), $templateCode);
		if( $dvprint) $templateCode = str_replace('<%%VALUE(decision_status_remarks_by_superior)%%>', safe_html($urow['decision_status_remarks_by_superior']), $templateCode);
		if(!$dvprint) $templateCode = str_replace('<%%VALUE(decision_status_remarks_by_superior)%%>', html_attr($row['decision_status_remarks_by_superior']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_status_remarks_by_superior)%%>', urlencode($urow['decision_status_remarks_by_superior']), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_by)%%>', safe_html($urow['created_by']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_by)%%>', urlencode($urow['created_by']), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_at)%%>', safe_html($urow['created_at']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_at)%%>', urlencode($urow['created_at']), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_by)%%>', safe_html($urow['last_updated_by']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_by)%%>', urlencode($urow['last_updated_by']), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_at)%%>', safe_html($urow['last_updated_at']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_at)%%>', urlencode($urow['last_updated_at']), $templateCode);
		$templateCode = str_replace('<%%VALUE(meetings_decision_str)%%>', safe_html($urow['meetings_decision_str']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(meetings_decision_str)%%>', urlencode($urow['meetings_decision_str']), $templateCode);
		$templateCode = str_replace('<%%VALUE(tenant_id)%%>', safe_html($urow['tenant_id']), $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tenant_id)%%>', urlencode($urow['tenant_id']), $templateCode);
	} else {
		$templateCode = str_replace('<%%VALUE(id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(id)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(agenda_lookup)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(agenda_lookup)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(decision_description)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_description)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(decision_actor)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_actor)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(action_taken_with_date)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(action_taken_with_date)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(decision_status)%%>', 'Yet to Start', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_status)%%>', urlencode('Yet to Start'), $templateCode);
		$templateCode = str_replace('<%%VALUE(decision_status_update_date)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_status_update_date)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(decision_status_remarks_by_superior)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(decision_status_remarks_by_superior)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_by)%%>', '<%%creatorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_by)%%>', urlencode('<%%creatorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(created_at)%%>', '<%%creationDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(created_at)%%>', urlencode('<%%creationDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_by)%%>', '<%%editorUsername%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_by)%%>', urlencode('<%%editorUsername%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(last_updated_at)%%>', '<%%editingDateTime%%>', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(last_updated_at)%%>', urlencode('<%%editingDateTime%%>'), $templateCode);
		$templateCode = str_replace('<%%VALUE(meetings_decision_str)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(meetings_decision_str)%%>', urlencode(''), $templateCode);
		$templateCode = str_replace('<%%VALUE(tenant_id)%%>', '', $templateCode);
		$templateCode = str_replace('<%%URLVALUE(tenant_id)%%>', urlencode(''), $templateCode);
	}

	// process translations
	$templateCode = parseTemplate($templateCode);

	// clear scrap
	$templateCode = str_replace('<%%', '<!-- ', $templateCode);
	$templateCode = str_replace('%%>', ' -->', $templateCode);

	// hide links to inaccessible tables
	if(Request::val('dvprint_x') == '') {
		$templateCode .= "\n\n<script>\$j(function() {\n";
		$arrTables = getTableList();
		foreach($arrTables as $name => $caption) {
			$templateCode .= "\t\$j('#{$name}_link').removeClass('hidden');\n";
			$templateCode .= "\t\$j('#xs_{$name}_link').removeClass('hidden');\n";
		}

		$templateCode .= $jsReadOnly;
		$templateCode .= $jsEditable;

		if(!$hasSelectedId) {
		}

		$templateCode.="\n});</script>\n";
	}

	// ajaxed auto-fill fields
	$templateCode .= '<script>';
	$templateCode .= '$j(function() {';


	$templateCode.="});";
	$templateCode.="</script>";
	$templateCode .= $lookups;

	// handle enforced parent values for read-only lookup fields
	$filterField = Request::val('FilterField');
	$filterOperator = Request::val('FilterOperator');
	$filterValue = Request::val('FilterValue');
	if(isset($filterField[1]) && $filterField[1] == '2' && $filterOperator[1] == '<=>')
		$templateCode.="\n<input type=hidden name=agenda_lookup value=\"" . html_attr($filterValue[1]) . "\">\n";

	// don't include blank images in lightbox gallery
	$templateCode = preg_replace('/blank.gif" data-lightbox=".*?"/', 'blank.gif"', $templateCode);

	// don't display empty email links
	$templateCode=preg_replace('/<a .*?href="mailto:".*?<\/a>/', '', $templateCode);

	/* default field values */
	$rdata = $jdata = get_defaults('meetings_decision_table');
	if($hasSelectedId) {
		$jdata = get_joined_record('meetings_decision_table', $selectedId);
		if($jdata === false) $jdata = get_defaults('meetings_decision_table');
		$rdata = $row;
	}
	$templateCode .= loadView('meetings_decision_table-ajax-cache', ['rdata' => $rdata, 'jdata' => $jdata]);

	// hook: meetings_decision_table_dv
	if(function_exists('meetings_decision_table_dv')) {
		$args = [];
		meetings_decision_table_dv(($hasSelectedId ? $selectedId : FALSE), getMemberInfo(), $templateCode, $args);
	}

	return $templateCode;
}