function meetings_table_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('meetings_table');
	if(!$arrPerm['insert']) {
		$error_message = $Translation['no insert permission'];
		return false;
	}

	// automatic visiting_card_lookup if passed as filterer
	if(Request::val('filterer_visiting_card_lookup')) {
		$_REQUEST['visiting_card_lookup'] = Request::val('filterer_visiting_card_lookup');
	}

	// automatic event_lookup if passed as filterer
	if(Request::val('filterer_event_lookup')) {
		$_REQUEST['event_lookup'] = Request::val('filterer_event_lookup');
	}

	$data = [
		'meeting_title' => Request::val('meeting_title', ''),
		'participants' => br2nl(Request::val('participants', '')),
		'venue' => Request::val('venue', ''),
		'meeting_from_date' => Request::dateComponents('meeting_from_date', '1'),
		'meeting_to_date' => Request::dateComponents('meeting_to_date', '1'),
		'created_by' => parseCode('<%%creatorUsername%%>', true),
		'created_at' => parseCode('<%%creationDateTime%%>', true),
	];


	// automatic visiting_card_lookup if passed as filterer
	if(Request::val('filterer_visiting_card_lookup')) {
		$data['visiting_card_lookup'] = Request::val('filterer_visiting_card_lookup');
	}

	// automatic event_lookup if passed as filterer
	if(Request::val('filterer_event_lookup')) {
		$data['event_lookup'] = Request::val('filterer_event_lookup');
	}
	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('meetings_table', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		meetings_table_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

