function meetings_table_update(&$selected_id, &$error_message = '') {
	global $Translation;

	// mm: can member edit record?
	if(!check_record_permission('meetings_table', $selected_id, 'edit')) return false;

	$data = [
		'meeting_title' => Request::val('meeting_title', ''),
		'participants' => br2nl(Request::val('participants', '')),
		'venue' => Request::val('venue', ''),
		'meeting_from_date' => Request::dateComponents('meeting_from_date', ''),
		'meeting_to_date' => Request::dateComponents('meeting_to_date', ''),
		'last_updated_by' => parseCode('<%%editorUsername%%>', false),
		'last_updated_at' => parseCode('<%%editingDateTime%%>', false),
	];

	// get existing values
	$old_data = getRecord('meetings_table', $selected_id);
	if(is_array($old_data)) {
		$old_data = array_map('makeSafe', $old_data);
		$old_data['selectedID'] = makeSafe($selected_id);
	}

	$data['selectedID'] = makeSafe($selected_id);

	// hook: meetings_table_before_update
	if(function_exists('meetings_table_before_update')) {
		$args = ['old_data' => $old_data];
		if(!meetings_table_before_update($data, getMemberInfo(), $args)) {
			if(isset($args['error_message'])) $error_message = $args['error_message'];
			return false;
		}
	}

	$set = $data; unset($set['selectedID']);
	foreach ($set as $field => $value) {
		$set[$field] = ($value !== '' && $value !== NULL) ? $value : NULL;
	}

	if(!update(
		'meetings_table', 
		backtick_keys_once($set), 
		['`id`' => $selected_id], 
		$error_message
	)) {
		echo $error_message;
		echo '<a href="meetings_table_view.php?SelectedID=' . urlencode($selected_id) . "\">{$Translation['< back']}</a>";
		exit;
	}


	update_calc_fields('meetings_table', $data['selectedID'], calculated_fields()['meetings_table']);

	// hook: meetings_table_after_update
	if(function_exists('meetings_table_after_update')) {
		if($row = getRecord('meetings_table', $data['selectedID'])) $data = array_map('makeSafe', $row);

		$data['selectedID'] = $data['id'];
		$args = ['old_data' => $old_data];
		if(!meetings_table_after_update($data, getMemberInfo(), $args)) return;
	}

	// mm: update record update timestamp
	set_record_owner('meetings_table', $selected_id);
}

