function visiting_card_table_insert(&$error_message = '') {
	global $Translation;

	// mm: can member insert record?
	$arrPerm = getTablePermissions('visiting_card_table');
	if(!$arrPerm['insert']) {
		$error_message = $Translation['no insert permission'];
		return false;
	}

	$data = [
		'name' => Request::val('name', ''),
		'recommended_by' => Request::val('recommended_by', ''),
		'designation' => Request::val('designation', ''),
		'company_name' => Request::val('company_name', ''),
		'mobile_no' => Request::val('mobile_no', ''),
		'email' => Request::val('email', ''),
		'company_website_addr' => Request::val('company_website_addr', ''),
		'given_by' => Request::lookup('given_by', ''),
		'suggested_way_forward' => Request::val('suggested_way_forward', ''),
		'front_img' => Request::fileUpload('front_img', [
			'maxSize' => 10240000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
				Thumbnail::create($name, getThumbnailSpecs('visiting_card_table', 'front_img', 'tv'));
				Thumbnail::create($name, getThumbnailSpecs('visiting_card_table', 'front_img', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!strlen(Request::val('SelectedID'))) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('visiting_card_table', 'front_img', Request::val('SelectedID'));
			},
		]),
		'back_img' => Request::fileUpload('back_img', [
			'maxSize' => 10240000,
			'types' => 'jpg|jpeg|gif|png|webp',
			'noRename' => false,
			'dir' => '',
			'success' => function($name, $selected_id) {
				Thumbnail::create($name, getThumbnailSpecs('visiting_card_table', 'back_img', 'tv'));
				Thumbnail::create($name, getThumbnailSpecs('visiting_card_table', 'back_img', 'dv'));
			},
			'failure' => function($selected_id, $fileRemoved) {
				if(!strlen(Request::val('SelectedID'))) return '';

				/* for empty upload fields, when saving a copy of an existing record, copy the original upload field */
				return existing_value('visiting_card_table', 'back_img', Request::val('SelectedID'));
			},
		]),
		'created_by' => parseCode('<%%creatorUsername%%>', true),
		'created_at' => parseCode('<%%creationDateTime%%>', true),
	];

	// record owner is current user
	$recordOwner = getLoggedMemberID();

	$recID = tableInsert('visiting_card_table', $data, $recordOwner, $error_message);

	// if this record is a copy of another record, copy children if applicable
	if(strlen(Request::val('SelectedID')) && $recID !== false)
		visiting_card_table_copy_children($recID, Request::val('SelectedID'));

	return $recID;
}

